// Get current language from localStorage or default to English
function getCurrentLanguage() {
  return localStorage.getItem('language') || 'en';
}

// Set language
function setLanguage(lang) {
  localStorage.setItem('language', lang);
  updatePageContent(lang);
}

// Translate text
function t(key) {
  const lang = getCurrentLanguage();
  return translations[lang][key] || translations.en[key] || key;
}

// Update page content with translations
function updatePageContent(lang) {
  console.log('Updating language to:', lang);
  
  // Update all elements with data-i18n attribute
  document.querySelectorAll('[data-i18n]').forEach(element => {
    const key = element.getAttribute('data-i18n');
    const translation = translations[lang] && translations[lang][key] ? translations[lang][key] : (translations.en[key] || key);
    element.textContent = translation;
    console.log('Updated:', key, '→', translation);
  });
  
  // Update all elements with data-i18n-html attribute
  document.querySelectorAll('[data-i18n-html]').forEach(element => {
    const key = element.getAttribute('data-i18n-html');
    const translation = translations[lang] && translations[lang][key] ? translations[lang][key] : (translations.en[key] || key);
    element.innerHTML = translation;
    console.log('Updated HTML:', key, '→', translation.substring(0, 50) + '...');
  });
  
  // Update all elements with data-i18n-placeholder attribute
  document.querySelectorAll('[data-i18n-placeholder]').forEach(element => {
    const key = element.getAttribute('data-i18n-placeholder');
    const translation = translations[lang] && translations[lang][key] ? translations[lang][key] : (translations.en[key] || key);
    element.setAttribute('placeholder', translation);
    console.log('Updated placeholder:', key, '→', translation);
  });
  
  // Update page lang attribute
  document.documentElement.lang = lang;
  
  // Update active lang switch
  document.querySelectorAll('.lang-switch__link').forEach(link => {
    link.classList.remove('lang-active');
    if (link.getAttribute('data-lang') === lang) {
      link.classList.add('lang-active');
    }
  });
  
  console.log('Language update completed for:', lang);
}

// Initialize translations on page load
document.addEventListener('DOMContentLoaded', () => {
  const currentLang = getCurrentLanguage();
  updatePageContent(currentLang);
  
  // Set up language switcher
  document.querySelectorAll('.lang-switch__link').forEach(link => {
    link.addEventListener('click', (e) => {
      e.preventDefault();
      const lang = link.getAttribute('data-lang');
      setLanguage(lang);
    });
  });
});


